// Process Apollo CSV file data from Read File node
const fileContent = $input.first().binary.data.toString('utf8');

// Split into lines and process
const lines = fileContent.trim().split('\n');
const headers = lines[0].split('\t');
const dataRows = lines.slice(1);

// Convert to objects
const contacts = dataRows.map(row => {
  const fields = row.split('\t');
  const contact = {};
  headers.forEach((header, index) => {
    contact[header] = fields[index] || '';
  });
  return contact;
});

// Filter for facilities contacts
const facilitiesContacts = contacts.filter(contact => {
  const email = contact['Email'];
  const title = contact['Title'].toLowerCase();
  const department = contact['Departments'].toLowerCase();

  return email && 
         email.includes('@') && 
         !email.includes('example.com') &&
         (title.includes('facilities') || 
          title.includes('facility') || 
          title.includes('maintenance') ||
          title.includes('operations') ||
          department.includes('facilities') ||
          department.includes('operations'));
});

console.log(`âœ… Loaded ${contacts.length} contacts from file`);
console.log(`ðŸŽ¯ Found ${facilitiesContacts.length} facilities contacts`);

return { json: { records: facilitiesContacts } };

// Convert Apollo CSV format to Terra Verde email campaign format
const input = $input.first();

if (!input.json || !input.json.records) {
  throw new Error('No Apollo records received');
}

const apolloRecords = input.json.records;
console.log(`Converting ${apolloRecords.length} Apollo records to Terra Verde format...`);

const terraVerdeProspects = apolloRecords.map(apollo => {
  // Map Apollo fields to Terra Verde email campaign fields
  const prospect = {
    // Basic prospect info (matching Terra Verde email campaign structure)
    'First Name': apollo['First Name'] || '',
    'Last Name': apollo['Last Name'] || '',
    'Company': apollo['Company'] || apollo['Company Name for Emails'] || '',
    'Primary Email': apollo['Email'] || '',
    'Title': apollo['Title'] || '',
    'Industry': apollo['Industry'] || 'Business Services', // Default industry
    'Phone': apollo['Work Direct Phone'] || apollo['Mobile Phone'] || apollo['Corporate Phone'] || '',
    'City': apollo['City'] || '',
    'State': apollo['State'] || '',
    'Country': apollo['Country'] || '',
    'LinkedIn': apollo['Person Linkedin Url'] || '',
    'Website': apollo['Website'] || '',

    // Campaign tracking fields (all new prospects start as "New")
    'Status': 'New',
    'Intro_Sent_Date': '',
    'Followup_Sent_Date': '',
    'Final_Sent_Date': '',
    'Response': '',
    'Appointment_Booked': '',
    'Next_Action': '',
    'Last_Updated': ''
  };

  console.log(`âœ… Converted: ${prospect['First Name']} ${prospect['Last Name']} (${prospect['Company']})`);
  return prospect;
});

console.log(`ðŸŽ¯ Converted ${terraVerdeProspects.length} Apollo prospects to Terra Verde format`);

// Return in the format expected by the email campaign logic
return terraVerdeProspects.map(prospect => ({ json: prospect }));

const today = new Date();
today.setHours(0, 0, 0, 0);

const results = [];
let introACount = 0;
let introBCount = 0;

for (const item of $input.all()) {
  const data = item.json;

  console.log('ðŸ“‹ Processing prospect:', data['First Name'], data['Last Name']);

  const prospect = {
    // Basic prospect info
    First_Name: data['First Name'] || '',
    Last_Name: data['Last Name'] || '',
    Company_Name: data['Company'] || '',
    Email: data['Primary Email'] || '',
    Title: data['Title'] || '',
    Industry: data['Industry'] || '',
    Phone: data['Phone'] || '',
    LinkedIn: data['LinkedIn'] || '',
    Website: data['Website'] || '',
    City: data['City'] || '',
    State: data['State'] || '',
    Country: data['Country'] || '',

    // Tracking fields
    Contact_Name: `${data['First Name'] || ''} ${data['Last Name'] || ''}`.trim(),
    Status: data['Status'] || 'New',
    Intro_Sent_Date: data['Intro_Sent_Date'] || '',
    Followup_Sent_Date: data['Followup_Sent_Date'] || '',
    Final_Sent_Date: data['Final_Sent_Date'] || '',
    Response: data['Response'] || '',
    Appointment_Booked: data['Appointment_Booked'] || '',
    Next_Action: data['Next_Action'] || '',
    Last_Updated: data['Last_Updated'] || '',

    // Logic flags
    today: today.toISOString().split('T')[0],
    needsIntroEmail: false,
    needsFollowupEmail: false,
    needsFinalEmail: false,
    emailType: '',
    emailVersion: '',
    emailStatus: 0
  };

  // Skip if no email
  if (!prospect.Email) {
    console.log('âš ï¸ Skipping prospect without email:', prospect.Contact_Name);
    continue;
  }

  // EMAIL TIMING LOGIC
  console.log(`ðŸ” Analyzing ${prospect.Contact_Name}:`);
  console.log(`  Status: "${prospect.Status}"`);
  console.log(`  Intro Date: "${prospect.Intro_Sent_Date}"`);
  console.log(`  Followup Date: "${prospect.Followup_Sent_Date}"`);

  if (prospect.Followup_Sent_Date && prospect.Followup_Sent_Date !== '') {
    // Has followup date - check for final email
    const followupDate = new Date(prospect.Followup_Sent_Date);
    const daysDiff = Math.floor((today - followupDate) / (1000 * 60 * 60 * 24));
    console.log(`  Days since followup: ${daysDiff}`);

    if (daysDiff >= 3) {
      prospect.needsFinalEmail = true;
      prospect.emailType = 'final';
      prospect.emailVersion = 'FINAL';
      prospect.emailStatus = 4;
      console.log(`  âœ… FINAL email assigned`);
    }
  }
  else if (prospect.Intro_Sent_Date && prospect.Intro_Sent_Date !== '') {
    // Has intro date but no followup - check for followup email
    const introDate = new Date(prospect.Intro_Sent_Date);
    const daysDiff = Math.floor((today - introDate) / (1000 * 60 * 60 * 24));
    console.log(`  Days since intro: ${daysDiff}`);

    if (daysDiff >= 2) {
      prospect.needsFollowupEmail = true;
      prospect.emailType = 'followup';
      prospect.emailVersion = 'FOLLOWUP';
      prospect.emailStatus = 3;
      console.log(`  âœ… FOLLOWUP email assigned`);
    }
  }
  else {
    // New prospect - needs intro email with A/B testing
    prospect.needsIntroEmail = true;
    prospect.emailType = 'intro';

    // A/B Testing Logic - Alternate between A and B
    const version = (introACount + introBCount) % 2 === 0 ? 'A' : 'B';

    if (version === 'A') {
      introACount++;
      prospect.emailStatus = 1;
      prospect.emailVersion = 'INTRO-A';
      console.log(`  ðŸ”µ INTRO-A assigned to ${prospect.Contact_Name}`);
    } else {
      introBCount++;
      prospect.emailStatus = 2;
      prospect.emailVersion = 'INTRO-B';
      console.log(`  ðŸŸ£ INTRO-B assigned to ${prospect.Contact_Name}`);
    }
  }

  console.log(`  ðŸ“§ Final assignment: ${prospect.emailVersion}`);
  console.log('---');

  results.push(prospect);
}

console.log(`âœ… Campaign logic complete!`);
console.log(`ðŸ“Š Email distribution:`);
console.log(`  - Total prospects: ${results.length}`);
console.log(`  - INTRO-A assignments: ${introACount}`);
console.log(`  - INTRO-B assignments: ${introBCount}`);
console.log(`  - FOLLOWUP assignments: ${results.filter(r => r.emailVersion === 'FOLLOWUP').length}`);
console.log(`  - FINAL assignments: ${results.filter(r => r.emailVersion === 'FINAL').length}`);

return results.map(prospect => ({ json: prospect }));

// Process Apollo CSV data - correct format
const input = $input.first();

// Get the CSV data from the correct location
const csvText = input.json.data;

if (!csvText) {
  throw new Error('No CSV data found');
}

// Split into lines and process
const lines = csvText.trim().split('\n');
const headers = lines[0].split(',');
const dataRows = lines.slice(1);

// Convert to objects
const contacts = dataRows.map(row => {
  // Handle CSV with quoted fields properly
  const fields = row.match(/(".*?"|[^",]+)(?=\s*,|\s*$)/g) || [];
  const contact = {};
  headers.forEach((header, index) => {
    const value = fields[index] || '';
    // Remove quotes and clean up
    contact[header.trim()] = value.replace(/^"(.*)"$/, '$1').trim();
  });
  return contact;
});

// No filtering - use all contacts from the Apollo file
const facilitiesContacts = contacts.filter(contact => {
  const email = contact['Email'] || '';
  // Only check for valid email
  return email && email.includes('@') && !email.includes('example.com');
});

console.log(`Total contacts: ${contacts.length}`);
console.log(`Facilities contacts: ${facilitiesContacts.length}`);

// Return debug info in the output so we can see it
return { 
  json: { 
    records: facilitiesContacts,
    DEBUG: {
      totalContacts: contacts.length,
      facilitiesContacts: facilitiesContacts.length,
      allJobTitles: contacts.map(c => c['Title']).filter(t => t),
      filteredTitles: facilitiesContacts.map(c => c['Title'])
    }
  } 
};

